/** *  */package com.myb.api;import java.util.List;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.ResponseBody;import com.myb.dto.activity.ActivityTo;import com.myb.dto.activity.EventListParamTo;import com.myb.dto.infrastructure.MessageTo;import com.myb.dto.infrastructure.PageableTo;import com.myb.modul.activity.services.ActivityService;/** * @author warrior * */@Controller@RequestMapping("/activity")public class ActivityController {		@Autowired	private ActivityService service;	@RequestMapping("/hello")	public @ResponseBody MessageTo<String> helloworld() {		MessageTo<String> msg = new MessageTo<String>();		msg.error("xxxxx");		msg.setData("我是可以的吗？");		return msg;	}		@RequestMapping("/list")	@ResponseBody	public PageableTo<List<ActivityTo>> ActivityDto(PageableTo<EventListParamTo> page){		List<ActivityTo> pageableTo = service.getActivityList();		PageableTo<List<ActivityTo>> to = new PageableTo<List<ActivityTo>>();		to.setData(pageableTo);		return to;	}}