/** *  */package com.myb.dto.activity;import java.util.List;import lombok.Getter;import lombok.Setter;/** * @author warrior * */public class ActivityTo {		@Setter @Getter	private Long uid;		/**	 * 活动标题	 */	@Setter @Getter	private String title;	/**	 * 主办方	 */	@Setter @Getter	private String creatUser;	/**	 * 活动状态(未开始，进行中，已结束)	 */	@Setter @Getter	private String status;	/**	 * 活动种类	 */	private String eventSort;	/**	 * 活动类型	 */	private String eventType;	/**	 * 报名时间	 */	@Setter @Getter	private String signTime;	/**	 * 活动时间	 */	@Setter @Getter	private String eventTime;	/**	 * 活动位置信息	 */	@Setter @Getter	private LocationInfoTo eventLocationInfo;	/**	 * 活动信息	 */	@Setter @Getter	private String eventDetailInfo;	/**	 * 商户Id	 */	@Setter @Getter	private Long unitId;	/**	 * 图片地址集合	 */	@Setter @Getter	private List<String> picAddressList;	/**	 * 评论数	 */	@Setter @Getter	private int commentNum;	/**	 * 照片墙数	 */	@Setter @Getter	private int picWallNum;	/**	 * 手机号报名限制，0：不限制报名次数，可重复报名 1：一个手机号一个活动限制报名一次	 */	@Setter @Getter	private Short limitMobile;	/**	 * 活动内容，html格式	 */	@Setter @Getter	private String activityDetailsHTML;	/**	 * 控制喜欢，0不限制，1非活动期间不能喜欢；	 */	@Setter @Getter	private int controlLove;	/**	 * 上传照片是否需要报名（必填），1：是 0：否，默认为0	 */	@Setter @Getter	private int isPicToSign;	/**	 * 适用商户数	 */	@Setter @Getter	private int inUseUnitCount;	/**	 * 使用场次数	 */	@Setter @Getter	private int inUseAddressCount;	/**	 * 最近的适用场次	 */	@Setter @Getter	private InfoActAddressTo actAddressTo;	/**	 * 最近的适用商户	 */	@Setter @Getter	private ApplicableUnitTo applicableUnitTo;	/**	 * 报名人数是否已满	 */	@Setter @Getter	private boolean isNumFull;	/**	 * 标识有无场次，1活动，2有适用商户，3有适用场次	 */	@Setter @Getter	private int eventFlag = 1;	/**	 * 举办单位的单位数	 */	@Setter @Getter	private int inJoinUnitCount;	/**	 * 最近的举办单位	 */	@Setter @Getter	private ApplicableUnitTo nearByCreateUnitTo;	/**	 * 最近举办单位的位置信息	 */	@Setter @Getter	private LocationInfoTo createUnitLocationInfoTo;	/**	 * 活动年龄限制范围	 */	@Setter @Getter	private String ageRange;}