/** *  */package com.myb.dto.activity;import java.io.Serializable;import lombok.Getter;import lombok.Setter;/** * @author warrior * */public class EventListParamTo implements Serializable {	private static final long serialVersionUID = 72813424629583867L;	/**	 * 用户id，用于查询用户报名的活动和关注的活动	 */	@Setter @Getter	private long userId;	/**	 * 纬度	 */	@Setter @Getter	private double lat;	/**	 * 经度	 */	@Setter @Getter	private double lng;	/**	 * 普通查询 CommonInfo.SEARCHFLAG_NORMAL=0;<br>	 * 查询用户关注的活动CommonInfo.SEARCHFLAG_ATT=1;<br>	 * 查询用户报名的活动 CommonInfo.SEARCHFLAG_My=2;	 */	@Setter @Getter	private int searchFlag;	/**	 * 用于年龄的过滤<br>	 * 默认值为：-99（标识全部）	 */	@Setter @Getter	private int customAge = -99;	/**	 * 市ID	 */	@Setter @Getter	private long cityId;	/**	 * 区域ID	 */	@Setter @Getter	private long placeId;	/**	 * 活动种类，关联表 t_info_ActivityDic，取TypeTag=1的数据，如产后恢复、比赛、培训等	 */	@Setter @Getter	private long eventCategory;	/**	 * 活动种类，关联表 t_info_ActivityDic，取TypeTag=3的数据，如公益活动、商业活动。	 */	@Setter @Getter	private long eventType;	/**	 * 机构id，查询该机构的活动	 */	@Setter @Getter	private long unitId;	/**	 * 排序类型 参照ListSearchTo	 */	@Setter @Getter	private long sortType;	/**	 * 客户端类型<br>	 * 母婴宝客户端：51 139母婴宝客户端：139 默认为51	 */	@Setter @Getter	private int clientType = 51;		/**	 * 查询 0正常查询 1品牌关联商户 2商品关联商户	 */	@Setter @Getter	private int searchType = 0;	/**	 * 品牌id	 */	@Setter @Getter	private long brandId;	/**	 * 商品id	 */	@Setter @Getter	private long itemId;}