package com.myb.dto.infrastructure;import java.io.Serializable;import lombok.Getter;import lombok.Setter;public class MessageTo<T> implements Serializable{	/**	 * 	 */	private static final long serialVersionUID = 1L;	/***	 * 接口版本	 */	@Setter @Getter	private int version;	/***	 * 数据调用来源	 */	@Setter @Getter	private String source;	/***	 * 数据调用key	 */	@Setter @Getter	private String sign;	/***	 * 标志位	 */	@Setter @Getter	private Boolean success;	/***	 * 描述信息	 */	@Setter @Getter	private String desc;	/***	 * 附加的数据	 */	@Setter @Getter	private T data;		public MessageTo<T> error(String desc){		this.desc = desc;		this.success = false;		return this;	}}