/** *  */package com.myb.handle;import java.sql.Types;import org.hibernate.dialect.SQLServer2008Dialect;import org.hibernate.type.StandardBasicTypes;/** * @author warrior * 重写定义数据库方言 * */public class CustomDialect extends SQLServer2008Dialect {	public CustomDialect(){		super();		registerHibernateType(Types.CHAR, StandardBasicTypes.STRING.getName());           registerHibernateType(Types.NVARCHAR, StandardBasicTypes.STRING.getName());           registerHibernateType(Types.LONGNVARCHAR, StandardBasicTypes.STRING.getName());           registerHibernateType(Types.DECIMAL, StandardBasicTypes.DOUBLE.getName());	}}