/** *  */package com.myb.modul.activity.services.impl;import java.util.ArrayList;import java.util.List;import java.util.function.Consumer;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.data.domain.PageRequest;import org.springframework.data.domain.Pageable;import org.springframework.data.domain.Sort.Direction;import org.springframework.stereotype.Service;import com.myb.dto.activity.ActivityTo;import com.myb.dto.activity.EventItemTo;import com.myb.dto.activity.EventListParamTo;import com.myb.dto.infrastructure.PageableTo;import com.myb.modul.activity.assembler.ActivityToAssembler;import com.myb.modul.activity.domain.T_info_Activity;import com.myb.modul.activity.repository.ActivityRepository;import com.myb.modul.activity.services.ActivityService;/** * @author warrior * */@Service("activityService")public class ActivityServiceImpl implements ActivityService {	@Autowired	private ActivityRepository repository;		private ActivityToAssembler ata = new ActivityToAssembler();		public void updateActivity(T_info_Activity activity) {		repository.save(activity);	}	/* (non-Javadoc)	 * @see com.myb.modul.activity.services.IActivityService#getActivityList()	 */	@Override	public List<ActivityTo> getActivityList() {		final List<ActivityTo> result = new ArrayList<ActivityTo>();		Pageable pageable = new PageRequest(1, 20, Direction.DESC, "id");		Iterable<T_info_Activity> iterable = repository.findAll(pageable);		iterable.forEach(new Consumer<T_info_Activity>() {			@Override			public void accept(T_info_Activity t) {				result.add(ata.toTo(t));			}		});		return result;	}	/* (non-Javadoc)	 * @see com.myb.modul.activity.services.IActivityService#findActivityList(com.myb.dto.infrastructure.PageableTo)	 */	@Override	public PageableTo<List<EventItemTo>> findActivityList(			PageableTo<EventListParamTo> pageableTo) {				return null;	}	}